import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import './App.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash } from '@fortawesome/free-solid-svg-icons';

function App() {
  // State to keep track of rows
  const [rows, setRows] = useState([]);
  // State to store the current input value
  const [inputValue, setInputValue] = useState('');

  // Function to handle adding a new row
  const handleAddRow = () => {
    console.log("add row");
    if (inputValue.trim() !== '') {
      // Create a new row object
      const newRow = {
        id: Date.now(),
        content: inputValue,
      };

      // Update the rows state with the new row
      setRows((prevRows) => [...prevRows, newRow]);

      // Clear the input value
      setInputValue('');
    }
  };

  // Function to handle deleting a row
  const handleDeleteRow = (id) => {
    setRows((prevRows) => prevRows.filter((row) => row.id !== id));
  };

  return (
    <div className="container-fluid text-center">
      <div className="title mt-6 text-white">
        <h1>Make Your Plan</h1>
      </div>

    <form className="inputField" onSubmit={(e) => e.preventDefault()}>
      <div className="container p-3 p-md-5 my-5 lg-dark custom-background text-white d-flex flex-column align-items-center">
        {/* Input for adding new rows */}
        <div className="mb-2 d-flex flex-column flex-md-row align-items-md-center" style={{ display: 'flex', alignItems: 'center', position: 'relative'}}>
          <input
            type="text"
            className="form-control mb-2 mb-md-0"
            value={inputValue}
            onChange={(e) => {
              setInputValue(e.target.value);
              console.log('Input value:', e.target.value);
            }}
            placeholder="Enter to-do"
          />
          <button className="ml-md-2" onClick={handleAddRow} style={{ position: 'absolute', right: '0', top: '0' }}>
            +
          </button>
        </div>
<br/>
        {/* Display table of existing rows */}
        <table className="table custom-rounded-table table-hover">
          <thead>
            <tr>
              <th>To-do</th>
            </tr>
          </thead>
          <tbody>
            {rows.map((row) => (
              <tr className="rounded" key={row.id}>
                <td>{row.content}</td>
                <button className="delete-button" onClick={() => handleDeleteRow(row.id)}>
                  <FontAwesomeIcon icon={faTrash} />
                </button>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      </form>
    </div>
  );
}

export default App;
