import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import './App.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash } from '@fortawesome/free-solid-svg-icons';

function App() {

  // State to keep track of rows
  const [rows, setRows] = useState([]);

  // Function to handle adding a new row
  const handleAddRow = () => {
    // Create a new row object
    const newRow = {
      id: Date.now(),
      content: '',
    };
    // Update the rows state with the new row
    setRows((prevRows) => [...prevRows, newRow]);
  };

  // Function to handle deleting a row
  const handleDeleteRow = (id) => {
    setRows((prevRows) => prevRows.filter((row) => row.id !== id));
  };

  return (
    <div className="container">
      <div className="container mt-5">
        <h1>Make Your Plan</h1>
      </div>

      <div className="container p-5 my-5 l-dark text-white">
        {/* Display existing rows */}
        {rows.map((row) => (
          <div key={row.id} className="mb-2 d-flex align-items-center">
            <input
              type="text"
              className="form-control"
              value={row.content}
              onChange={(e) => {
                // Update the content of the row when input changes
                setRows((prevRows) =>
                  prevRows.map((prevRow) =>
                    prevRow.id === row.id ? { ...prevRow, content: e.target.value } : prevRow
                  )
                );
              }}
            />
            <button className="ml-2" onClick={() => handleDeleteRow(row.id)}>
              <FontAwesomeIcon icon={faTrash} />
            </button>
          </div>
        ))}

        <br />

        {/* Add button to add a new row */}
        <button className="btn btn-primary" onClick={handleAddRow}>
          Add
        </button>
      </div>
    </div>
  );
}

export default App;
