import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import './App.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash } from '@fortawesome/free-solid-svg-icons';

function App() {
  // State to keep track of rows
  const [rows, setRows] = useState([]);
  // State to store the current input value
  const [inputValue, setInputValue] = useState('');

  // Function to handle adding a new row
  const handleAddRow = () => {
    if (inputValue.trim() !== '') {
      // Create a new row object
      const newRow = {
        id: Date.now(),
        content: inputValue,
      };

      // Update the rows state with the new row
      setRows((prevRows) => [...prevRows, newRow]);

      // Clear the input value
      setInputValue('');
    }
  };

  // Function to handle deleting a row
  const handleDeleteRow = (id) => {
    setRows((prevRows) => prevRows.filter((row) => row.id !== id));
  };

  return (
    <div className="container-fluid text-center">
      <div className="container mt-5">
        <h1>Make Your Plan</h1>
      </div>

      <div className="container p-3 p-md-5 my-5 lg-dark custom-background text-white d-flex flex-column align-items-center">
        {/* Input for adding new rows */}
        <div className="mb-2 d-flex flex-column flex-md-row align-items-md-center">
          <input
            type="text"
            className="form-control mb-2 mb-md-0 w-100"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            placeholder="Enter your to-do"
          />
          <button className="ml-md-2 btn btn-info" onClick={handleAddRow}>
            Add
          </button>
        </div>

        {/* Display table of existing rows */}
        <table className="table table-light custom-rounded-table table-hover w-50 w-md-50">
          <thead >
            <tr className="rounded">
              <th>To-do List</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody className="rounded">
            {rows.map((row) => (
              <tr key={row.id}>
                <td>{row.content}</td>
                <td>
                  <button onClick={() => handleDeleteRow(row.id)}>
                    <FontAwesomeIcon icon={faTrash} />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
